import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';




class Car extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }
	changeicon = () => { this.setState({value: this.state.options[0]}) }
  render() {
    return <div className="leftbox" onClick={this.changeicon}> {this.state.value}</div>;


  }
}

class Car1 extends React.Component {
   constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }
	changeicon = () => { this.setState({value: this.state.options[0]}) }

  render() {
    return <div className = "middlebox" onClick={this.changeicon}> </div> ;
  }
}


class Car2 extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      brand: "Ford",
      model: "Mustang",
      color: "red",
      year: 1964
    };
  }
	render(){
		return <div className = "rightbox" onClick={this.changeicon}> </div>;
	}

}
class Football extends React.Component {
  shoot() {
    alert("Great Shot!");
  }
  render() {
    return (
	 <div style={{width:"100%",textAlign:"center",marginLeft:"35%"}}>
	 <div style={{paddingTop:"4%"}}> <Car value="check" /><Car1 /><Car2 />  </div>
      <div style={{paddingTop:"9%"}}> <Car /><Car1 /><Car2 />  </div>
	<div style={{paddingTop:"9%"}}> <Car /><Car1 /><Car2 />  </div>

	  </div>




    );
  }
}

ReactDOM.render(<Football />, document.getElementById('root'));
