import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

var prevState = "X"
var count = 0;
var gameMatrix = [[0,0,0],[0,0,0],[0,0,0]]
async function check()
{

	let promise = new Promise((resolve,reject)=>{
	let list = [gameMatrix[0][0]+gameMatrix[0][1]+gameMatrix[0][2],
				gameMatrix[1][0]+gameMatrix[1][1]+gameMatrix[1][2],
				gameMatrix[2][0]+gameMatrix[2][1]+gameMatrix[2][2],
				gameMatrix[0][0]+gameMatrix[1][1]+gameMatrix[2][2],
				gameMatrix[0][2]+gameMatrix[1][1]+gameMatrix[2][0],
				gameMatrix[0][0]+gameMatrix[1][0]+gameMatrix[2][0],
				gameMatrix[0][1]+gameMatrix[1][1]+gameMatrix[2][1],
				gameMatrix[0][2]+gameMatrix[1][2]+gameMatrix[2][2]]

	if ( list.includes("OOO"))
	{
		resolve("Player1 Won The Game")


	}
	else if(list.includes("XXX"))
	{

		resolve("Player2 Won The Game")
	}
	else
	{
		reject("NotDone")
	}

	});

	var result = await promise;

	alert(result)
	window.location.reload()
	//return "1"
}



class Car extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"],
		count: 0,
		prevStat: "X"


    };
  }


  ren = () =>{}


componentDidUpdate(prevProps, prevState) {
  if (prevState.value !== this.state.value) {
	  this.ren()
}}

	changeicon = () => {
		//alert(this.props.loc[0])
		//gameMatrix[this.props.loc[0]][this.props.loc[1]] = 

		if(prevState === "X" && this.state.value === " " && count < 8)
		{
			this.setState({value:"O"})
			prevState="O"
			count = count + 1
			gameMatrix[this.props.loc[0]][this.props.loc[1]] = "O"

		check()

		}
		else if(prevState === "O" && this.state.value === " "  && count < 8)
		{
			this.setState({value:"X"})
			prevState="X"	
			count = count + 1	
			gameMatrix[this.props.loc[0]][this.props.loc[1]] = "X"	
			check()	

		}
		else if(count === 8)
		{
			check()
			alert("Game Finished")
		}
		else
		{
			alert("Your have already played the block")
			alert(gameMatrix[0][0])
		}

	}


  render() {
    return <div><div className="leftbox" onClick={this.changeicon}> {this.state.value}</div></div>;


  }
}

