import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import { UserProvider } from './global.js'
import UserContext from './global.js'



function App() {
  const user = { name: 'Tania', loggedIn: true }

  return (
    <UserProvider value={user}>
      <Car />
    </UserProvider>
  )
}








class Car extends React.Component {
	static contextType = UserContext
  constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"],
		count: 0,


    };
  }



	changeicon = () => {}



  render() {
	  const user = this.context
    return <div className="leftbox" onClick={this.changeicon}> {user.name}</div>;


  }
}

class Car1 extends React.Component {
   constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }
	changeicon = () => { this.setState({value: this.state.options[1]}) }

  render() {
    return <div className = "middlebox" onClick={this.changeicon}> {this.state.value} </div> ;
  }
}


class Car2 extends React.Component{
   constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }


  updateCount = () => {alert()}


	changeicon = () => { this.setState({value: this.state.options[0]}) }

	render(){
		return <div className = "rightbox" onClick={this.changeicon}> {this.state.value} </div>;
	}

}
class Football extends React.Component {
  shoot() {
    alert("Great Shot!");
  }
  render() {
    return (
	 <div style={{width:"100%",textAlign:"center",marginLeft:"35%"}}>
	 <div style={{paddingTop:"4%"}}> <Car /><Car1 /><Car2 /> </div>
      <div style={{paddingTop:"9%"}}> <Car /><Car1 /><Car2 />  </div>
	<div style={{paddingTop:"9%"}}> <Car /><Car1 /><Car2 />  </div>

	  </div>




    );
  }
}

ReactDOM.render(<Football />, document.getElementById('root'));
