import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';


const MyContext = React.createContext("count");

class PlayerTurn extends React.Component{
	constructor(props) {
    super(props);
    this.state = {
		value:this.props.value + 1,

    };
  }

      render() {
        return (
           <MyContext.Provider value={{state: this.state}}>      
              <Child /> 
           </MyContext.Provider>
       )
    }

}


class Car extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }
	changeicon = () => { this.setState({value: MyContext.count}) }
  render() {
    return <div className="leftbox" onClick={this.changeicon}> {this.state.value}</div>;


  }
}

class Car1 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      brand: "Ford",
      model: "Mustang",
      color: "red",
      year: 1964
    };
  }

  render() {
    return <div className = "middlebox"> </div> ;
  }
}


class Car2 extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      brand: "Ford",
      model: "Mustang",
      color: "red",
      year: 1964
    };
  }
	render(){
		return <div className = "rightbox"> </div>;
	}

}
class Football extends React.Component {
  shoot() {
    alert("Great Shot!");
  }
  render() {
    return (
	 <div style={{width:"100%",textAlign:"center",marginLeft:"35%"}}>
	 <div style={{paddingTop:"4%"}}> <Car value="check" /><Car1 /><Car2 />  </div>
      <div style={{paddingTop:"9%"}}> <Car /><Car1 /><Car2 />  </div>
	<div style={{paddingTop:"9%"}}> <Car /><Car1 /><Car2 />  </div>

	  </div>




    );
  }
}

ReactDOM.render(<Football />, document.getElementById('root'));
