import React, { useContext } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import X from './X.jpg';
import O from './O.jpg'
import {GameBox} from './gamebox.js'

import { Route, Link, BrowserRouter as Router } from 'react-router-dom'

class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
	value : " ",
	squares : [" "," "," "," "," "," "," "," "," "]
    };
  }

  handleClick(i) {
	  const {squares} = this.state;
	  squares[i] = (squares[i] === "X" || squares[i] === "O") ? squares[i] : this.state.value === "X" ? "O" : "X";

	  this.state.value = squares[i]
	  this.setState({squares:squares})
  }
  renderSquare(i) {
	const {squares} = this.state;
    return (
      <Square
        value={squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  render() {
	let gameCheck = winnerCheck(this.state.squares)
	let status = " "
	if(gameCheck === "Tie Game" )
	{
	status = "It's a Tie Game"
	}
	else
	{
    status = gameCheck === "Game Completed" ?  "Player " + this.state.value + " Won Game" : (this.state.value === "X" ? "Next Player : O" : "Next Player : X" )
	}
    return (
      <div class="container">
	    <div ><Playerone value={status} /></div>

		<div>

        <div className="status-bar"><h3> {status} </h3></div>

        <div className="rowStyle">
          {this.renderSquare(0)}{this.renderSquare(1)}{this.renderSquare(2)}
        </div>
        <div className="rowStyle">
          {this.renderSquare(3)}{this.renderSquare(4)}{this.renderSquare(5)}
        </div>
        <div className="rowStyle">
          {this.renderSquare(6)}{this.renderSquare(7)}{this.renderSquare(8)}
        </div>
		</div>
		 <div ><Playertwo value={status} /></div>
      </div>
    );
  }
}

function Square(props) {
    const {value} = props;
	let icon = value === " " ? value : (value === "X" ? X : O)
    return (
      <button className="leftbox" onClick={props.onClick}>
        {     <img 
      src={icon}
       style={{width:"100%",height:"18vh",outline:"none"}}
      />}
      </button>
    );
}



function winnerCheck(x)
{
	const found = x.indexOf(" ")

	if((x[0]+x[1]+x[2] === "XXX" || x[0]+x[1]+x[2] === "OOO") || (x[3]+x[4]+x[5] === "XXX" || x[3]+x[4]+x[5] === "OOO") ||
	  (x[6]+x[7]+x[8] === "XXX" || x[6]+x[7]+x[8] === "OOO") || (x[0]+x[3]+x[6] === "XXX" || x[0]+x[3]+x[6] === "OOO") ||
	  (x[1]+x[4]+x[7] === "XXX" || x[1]+x[4]+x[7] === "OOO") || (x[2]+x[5]+x[8] === "XXX" || x[2]+x[5]+x[8] === "OOO") ||
	  (x[0]+x[4]+x[8] === "XXX" || x[0]+x[4]+x[8] === "OOO") || (x[2]+x[4]+x[6] === "XXX" || x[2]+x[4]+x[6] === "OOO") )
	  {
		  return "Game Completed"
	  }
	  else
	  {
		  if(found === -1)
		  {
		   return "Tie Game"
		  }
		  else
		  {
		  return x[found-1]
		  }
	  }
}




export class Playerone extends React.Component {
  constructor(props) {
    super(props)

  }
	 render(){
		 let XWins = 0;
		 let XDraws = 0;
		 let XLosts = 0;
		 let gameScore = this.props.value === "It's a Tie Game" ? XDraws+1 : this.props.value === "Player X Won Game" ? XWins+1 : XWins
		 let XLost = this.props.value === "Player O Won Game" ? XLosts+1 : XLosts
	 return(  
<div class="opponentBox1">
<h4>Player X </h4>
<h3>Matches Played : 0 </h3>
<h3>Matches Won : {XWins} </h3>
<h3>Matches Lost : 0 </h3>
<h3>Tie Matches : {XDraws} </h3>
</div>
  )
	 }
}

export class Playertwo extends React.Component {
  constructor(props) {
    super(props)

  }
	 render(){
		 let OWins = 0;
		 let ODraws = 0;
		 let gameScore = this.props.value === "It's a Tie Game" ? ODraws+1 : this.props.value === "Player O Won Game" ? OWins+1 : OWins
	 return(  
<div class="opponentBox2">
<h4>Player O </h4>
<h3>Matches Played : 0 </h3>
<h3>Matches Won : {OWins} </h3>
<h3>Matches Lost : 0 </h3>
<h3>Tie Matches : {ODraws} </h3>
</div>
  )
	 }
}


