import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';


const MyContext = React.createContext('test');

class playerTurn extends React.Component{
	    render() {
        return (
           <MyContext.Provider value={"0"}>      
              <Car/>
           </MyContext.Provider>
       )
    }
}

class Car extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"],

    };
  }

	changeicon = () => { this.setState({value: this.state.options[0]}) }

  render() {
    return <div className="leftbox" onClick={this.changeicon}> {this.props.value}</div>;


  }
}

class Car1 extends React.Component {
   constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }
	changeicon = () => { this.setState({value: this.state.options[1]}) }

  render() {
    return <div className = "middlebox" onClick={this.changeicon}> {this.state.value} </div> ;
  }
}


class Car2 extends React.Component{
   constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }
	changeicon = () => { this.setState({value: this.state.options[0]}) }

	render(){
		return <div className = "rightbox" onClick={this.changeicon}> {this.state.value} </div>;
	}

}
class Football extends React.Component {
  shoot() {
    alert("Great Shot!");
  }
  render() {
    return (
	 <div style={{width:"100%",textAlign:"center",marginLeft:"35%"}}>
	 <div style={{paddingTop:"4%"}}> <Car value={0} /><Car1 value="check" /><Car2 value="check" />  </div>
      <div style={{paddingTop:"9%"}}> <Car /><Car1 /><Car2 />  </div>
	<div style={{paddingTop:"9%"}}> <Car /><Car1 /><Car2 />  </div>

	  </div>




    );
  }
}

ReactDOM.render(<Football />, document.getElementById('root'));
