import React, { useContext } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import X from './X.jpg';
import O from './O.jpg'
import {GameBox} from './gamebox.js'

import { Route, Link, BrowserRouter as Router } from 'react-router-dom'

class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
	value : " ",
	squares : [" "," "," "," "," "," "," "," "," "]
    };
  }

  handleClick(i) {
	  const {squares} = this.state;
	  squares[i] = (squares[i] === "X" || squares[i] === "O") ? this.state.value : this.state.value === "X" ? "O" : "X";
	  this.state.value = squares[i]
	  this.setState({squares:squares})
  }
  renderSquare(i) {
	const {squares} = this.state;
    return (
      <Square
        value={squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  render() {
    let status = winnerCheck(this.state.squares) === "Game Completed" ?  "Player " + this.state.value + " Won Game" : (this.state.value === "X" ? "Next Player : O" : "Next Player : X" )

    return (
      <div class="container">
	    <div ><Playerone value={status} /></div>
		<div>
        <div className="status-bar"><h3> {status} </h3></div>
        <div className="rowStyle">
          {this.renderSquare(0)}{this.renderSquare(1)}{this.renderSquare(2)}
        </div>
        <div className="rowStyle">
          {this.renderSquare(3)}{this.renderSquare(4)}{this.renderSquare(5)}
        </div>
        <div className="rowStyle">
          {this.renderSquare(6)}{this.renderSquare(7)}{this.renderSquare(8)}
        </div>
		</div>
		 <div ><Playertwo value={status} /></div>
      </div>
    );
  }
}

function Square(props) {
    const {value} = props;
	let icon = value === " " ? value : (value === "X" ? X : O)
    return (
      <button className="leftbox" onClick={props.onClick}>
        {     <img 
      src={icon}
       style={{width:"100%",height:"18vh",outline:"none"}}
      />}
      </button>
    );
}



function winnerCheck(x)
{
	const found = x.indexOf(" ")

	if((x[0]+x[1]+x[2] === "XXX" || x[0]+x[1]+x[2] === "O") || (x[3]+x[4]+x[5] === "XXX" || x[3]+x[4]+x[5] === "O") ||
	  (x[6]+x[7]+x[8] === "XXX" || x[6]+x[7]+x[8] === "O") || (x[0]+x[3]+x[6] === "XXX" || x[0]+x[3]+x[6] === "O") ||
	  (x[1]+x[4]+x[5] === "XXX" || x[1]+x[4]+x[5] === "O") || (x[2]+x[5]+x[8] === "XXX" || x[2]+x[5]+x[8] === "O") ||
	  (x[0]+x[4]+x[8] === "XXX" || x[0]+x[4]+x[8] === "O") || (x[2]+x[4]+x[6] === "XXX" || x[2]+x[4]+x[6] === "O") )
	  {
		  return "Game Completed"
	  }
	  else
	  {
		  return x[found-1]
	  }
}




export class Playerone extends React.Component {
  constructor(props) {
    super(props)

  }
	 render(){
		 let gameScore = ""
	 return(  
<div class="opponentBox1">
<h4>Player {this.props.value} </h4>
<h3>Matches Played : 0 </h3>
<h3>Matches Won : 0 </h3>
<h3>Matches Lost : 0 </h3>
</div>
  )
	 }
}

export class Playertwo extends React.Component {
  constructor(props) {
    super(props)

  }
	 render(){
	 return(  
<div class="opponentBox2">
<h4>Player 2 </h4>
<h3>Matches Played : 0 </h3>
<h3>Matches Won : 0 </h3>
<h3>Matches Lost : 0 </h3>
</div>
  )
	 }
}


