import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import {GameBox} from './gamebox.js'


const NumberContext = React.createContext();

var prevState = "X"
var count = 0;
var gameMatrix = [[0,0,0],[0,0,0],[0,0,0]]
async function check()
{

	let promise = new Promise((resolve,reject)=>{
	let list = [gameMatrix[0][0]+gameMatrix[0][1]+gameMatrix[0][2],
				gameMatrix[1][0]+gameMatrix[1][1]+gameMatrix[1][2],
				gameMatrix[2][0]+gameMatrix[2][1]+gameMatrix[2][2],
				gameMatrix[0][0]+gameMatrix[1][1]+gameMatrix[2][2],
				gameMatrix[0][2]+gameMatrix[1][1]+gameMatrix[2][0],
				gameMatrix[0][0]+gameMatrix[1][0]+gameMatrix[2][0],
				gameMatrix[0][1]+gameMatrix[1][1]+gameMatrix[2][1],
				gameMatrix[0][2]+gameMatrix[1][2]+gameMatrix[2][2]]

	if ( list.includes("OOO"))
	{
		resolve("Player1 Won The Game")
		return 1;

	}
	else if(list.includes("XXX"))
	{
		return 1;
		resolve("Player2 Won The Game")
	}
	else
	{
		reject("NotDone")
	}

	});

	var result = await promise;

	alert(result)
	window.location.reload()
	//return "1"
}


function provider()
{

	return(
	<NumberContext.Provider value={42}>
	<Car />
