import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

var prevState = "X"
var count = 0;
var gameMatrix = [[0,0,0],[0,0,0],[0,0,0]]
function check()
{
	console.log("check")
}


class Car extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"],
		count: 0,
		prevStat: "X"

    };
  }

	changeicon = () => {
		//alert(this.props.loc[0])
		alert(gameMatrix[0][0])
		if(prevState === "X" && this.state.value === " " && count < 9)
		{
			this.setState({value:"O"})
			prevState="O"
			count = count + 1
			check()

		}
		else if(prevState === "O" && this.state.value === " "  && count < 9)
		{
			this.setState({value:"X"})
			prevState="X"	
			count = count + 1			
		}
		else
		{
			alert("Your have already played the block")
		}

	}


  render() {
    return <div className="leftbox" onClick={this.changeicon}> {this.state.value}</div>;


  }
}

class Car1 extends React.Component {
   constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }
	changeicon = () => { this.setState({value: this.state.options[1]}) }

  render() {
    return <div className = "middlebox" onClick={this.changeicon}> {this.state.value} </div> ;
  }
}


class Car2 extends React.Component{
   constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }


  updateCount = () => {alert()}


	changeicon = () => { this.setState({value: this.state.options[0]}) }

	render(){
		return <div className = "rightbox" onClick={this.changeicon}> {this.state.value} </div>;
	}

}
class Football extends React.Component {
  shoot() {
    alert("Great Shot!");
  }
  render() {
    return (
	 <div style={{width:"100%",textAlign:"center",marginLeft:"35%"}}>
	 <div style={{paddingTop:"4%"}}> <Car loc = {[0,0]}/><Car loc = {[0,1]}/><Car loc = {[0,2]}/> </div>
      <div style={{paddingTop:"9%"}}> <Car loc = {[1,0]}/><Car loc = {[1,1]}/><Car loc = {[1,2]}/>  </div>
	<div style={{paddingTop:"9%"}}> <Car loc = {[2,0]}/><Car loc = {[2,1]}/><Car loc = {[2,2]}/>  </div>

	  </div>




    );
  }
}

ReactDOM.render(<Football />, document.getElementById('root'));
