		//alert(this.props.loc[0])
		//gameMatrix[this.props.loc[0]][this.props.loc[1]] = 

		if(prevState === "X" && this.state.value === " " && count < 8)
		{
			this.setState({value:"O"})
			prevState="O"
			count = count + 1
			gameMatrix[this.props.loc[0]][this.props.loc[1]] = "O"
			check()

		}
		else if(prevState === "O" && this.state.value === " "  && count < 8)
		{
			this.setState({value:"X"})
			prevState="X"	
			count = count + 1	
			gameMatrix[this.props.loc[0]][this.props.loc[1]] = "X"	
			check()	

		}
		else if(count === 8)
		{
			check()
			alert("Game Finished")
		}
		else
		{
			alert("Your have already played the block")
			alert(gameMatrix[0][0])
		}

	}


  render() {

    return <div><div className="leftbox" onClick={this.changeicon}>{this.state.value}</div></div>;


  }
}







