{
	const found = x.indexOf(" ")

	if((x[0]+x[1]+x[2] === "XXX" || x[0]+x[1]+x[2] === "OOO") || (x[3]+x[4]+x[5] === "XXX" || x[3]+x[4]+x[5] === "OOO") ||
