import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';




class Car extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"],
		count: 0
    };
  }

	changeicon = () => { 
	if(this.state.count%2 == 0)
	{
		this.setState({value: this.state.options[0]}) 
	}
	else
	{
		this.setState({value: this.state.options[1]}) 
	}


	}

	updateCount = () => {this.setState({count:this.state.count+1,value:this.changeicon})}

  render() {
    return <div className="leftbox" onClick={this.updateCount}> {this.state.value}</div>;


  }
}

class Car1 extends React.Component {
   constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }
	changeicon = () => { this.setState({value: this.state.options[1]}) }

  render() {
    return <div className = "middlebox" onClick={this.changeicon}> {this.state.value} </div> ;
  }
}


class Car2 extends React.Component{
   constructor(props) {
    super(props);
    this.state = {
		value:" ",
		options:["X","O"]
    };
  }
	changeicon = () => { this.setState({value: this.state.options[0]}) }

	render(){
		return <div className = "rightbox" onClick={this.changeicon}> {this.state.value} </div>;
	}

}
class Football extends React.Component {
  shoot() {
    alert("Great Shot!");
  }
  render() {
    return (
	 <div style={{width:"100%",textAlign:"center",marginLeft:"35%"}}>
	 <div style={{paddingTop:"4%"}}> <Car value={0} /><Car/><Car/> </div>
      <div style={{paddingTop:"9%"}}><Car value={0} /><Car/><Car/>  </div>
	<div style={{paddingTop:"9%"}}> <Car value={0} /><Car/><Car/>  </div>

	  </div>




    );
  }
}

ReactDOM.render(<Football />, document.getElementById('root'));
